package org.java.spring;

import java.time.LocalDate; 
import java.util.List;

import org.java.spring.db.pojo.Ingredient;
import org.java.spring.db.pojo.Pizza;
import org.java.spring.db.pojo.SpecialOffer;
import org.java.spring.db.serv.IngredientService;
import org.java.spring.db.serv.PizzaService;
import org.java.spring.db.serv.SpecialOfferService;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.CommandLineRunner;
import org.springframework.boot.SpringApplication;
import org.springframework.boot.autoconfigure.SpringBootApplication;

@SpringBootApplication
public class SpringLaMiaPizzeriaRelazioniApplication implements CommandLineRunner {
	
	@Autowired
	private PizzaService pizzaService;
	
	@Autowired
	private SpecialOfferService specialOfferService;
	
	@Autowired
	private IngredientService ingredientService;
	
	public static void main(String[] args) {
		SpringApplication.run(SpringLaMiaPizzeriaRelazioniApplication.class, args);
	}

	@Override
	public void run(String... args) throws Exception {
		
		Ingredient ing1 = new Ingredient("Prosciutto crudo");
		Ingredient ing2 = new Ingredient("Prosciutto cotto");
		Ingredient ing3 = new Ingredient("Salame");
		Ingredient ing4 = new Ingredient("Mozzarella");
		Ingredient ing5 = new Ingredient("Pomodoro");
		Ingredient ing6 = new Ingredient("Farina");
		Ingredient ing7 = new Ingredient("Wurstel");
		Ingredient ing8 = new Ingredient("Stracchino");
		Ingredient ing9 = new Ingredient("Patatine");
		Ingredient ing10 = new Ingredient("Funghi");
		
		ingredientService.save(ing1);
		ingredientService.save(ing2);
		ingredientService.save(ing3);
		ingredientService.save(ing4);
		ingredientService.save(ing5);
		ingredientService.save(ing6);
		ingredientService.save(ing7);
		ingredientService.save(ing8);
		ingredientService.save(ing9);
		ingredientService.save(ing10);
		
		pizzaService.save(new Pizza("Capricciosa", "molto buona e capricciosa", "https://www.buttalapasta.it/wp-content/uploads/2017/11/pizza-capricciosa.jpg", 7.50, ing3, ing4, ing5, ing6));
		pizzaService.save(new Pizza("Wurstel", "buonisima con i wurstel", "https://www.dolcidee.it/media/uploads/recipe/5a5bb13339fd4_tmp26914248_2055067254519979_806649253_n.jpg", 7.00, ing4, ing5, ing6, ing7));
		pizzaService.save(new Pizza("Patatine", "fantastica questa", "https://www.dolcidee.it/media/uploads/recipe/5ede46927ca62_tmp6d9b772b4286436abd426b9e1c3769ba.jpg", 8.00, ing4, ing5, ing6, ing9));
		pizzaService.save(new Pizza("Prosciutto cotto", "questa la facciamo con il nostro prosciuttone", "https://efru.it/efru-upload/2020/03/pizza-prosciutto-cotto.jpg", 8.50, ing2, ing4, ing5, ing6));
		pizzaService.save(new Pizza("Prosciutto crudo", "questa la facciamo col prosciuttone", "data:image/jpeg;base64,", 8.50, ing1, ing4, ing5, ing6));
		
		List<Pizza> pizzas = pizzaService.findAll();
		
		specialOfferService.save(new SpecialOffer("offertina", LocalDate.now(), LocalDate.now().plusDays(7), pizzas.get(0)));
		specialOfferService.save(new SpecialOffer("offertina2", LocalDate.now(), LocalDate.now().plusDays(7), pizzas.get(0)));
		specialOfferService.save(new SpecialOffer("offerta", LocalDate.now(), LocalDate.now().plusDays(7), pizzas.get(1)));
		specialOfferService.save(new SpecialOffer("offerta2", LocalDate.now(), LocalDate.now().plusDays(7), pizzas.get(1)));
		specialOfferService.save(new SpecialOffer("offertona", LocalDate.now(), LocalDate.now().plusDays(7), pizzas.get(2)));
		specialOfferService.save(new SpecialOffer("offertona2", LocalDate.now(), LocalDate.now().plusDays(7), pizzas.get(2)));
		specialOfferService.save(new SpecialOffer("mega offerta", LocalDate.now(), LocalDate.now().plusDays(7), pizzas.get(3)));
		specialOfferService.save(new SpecialOffer("mega offerta2", LocalDate.now(), LocalDate.now().plusDays(7), pizzas.get(3)));
		specialOfferService.save(new SpecialOffer("offerta assurda", LocalDate.now(), LocalDate.now().plusDays(7), pizzas.get(4)));
		specialOfferService.save(new SpecialOffer("offerta assurda2", LocalDate.now(), LocalDate.now().plusDays(7), pizzas.get(4)));
		
	}

}
